create table dw_open_api_swagger
(
    id                    int auto_increment comment '自增id'
        primary key,
    api_name              varchar(255) null comment 'API名称',
    api_type              tinyint      null comment 'API类型',
    data_asset_api_method varchar(255) null comment 'API PATH',
    api_description       varchar(255) null comment 'api描述',
    parse_status          tinyint      null comment '解析结果，1 成功 2失败',
    error_detail          varchar(255) null comment '错误详情',
    api_info              longtext     null comment 'swagger的单个api的json数据',
    api_group_id          int          null comment '接口分组id',
    swagger_id            int          null comment 'swagger分组id',
    import_status         tinyint      null comment '临表导入正式表的api状态：1 导入成功 。2 导入失败',
    update_by             varchar(50)  null comment '更新人',
    update_time           datetime     null comment '更新时间',
    create_time           datetime     null comment '创建时间',
    create_by             varchar(50)  null comment '创建人',
    json_schema           text         null comment 'SwaggerApi参数(以jsonSchema形式)'
)
    comment 'swagger导入的临时表';

create index idx_swagger_id
    on dw_open_api_swagger (swagger_id);

